import React, { useState } from "react";
import Notification from "./Notification";

const Confirmation = (props) => {
// TODO your code here
const [acceptState, setAcceptState] = useState(false);
const [declineState, setDeclineState] = useState(false);


const handleAcceptClick = () => {
setAcceptState(true);
console.log('show message ' + acceptState);
}
const handleDeclineClick = () => {
setDeclineState(true);
console.log(declineState);
}

return (
<div>
<Notification className={props.type}
message={props.message} >
<button className="btn btn-primary" onClick={handleAcceptClick}>Yes</button><br/>
<button className="btn btn-danger" onClick={handleDeclineClick} >No</button>
</Notification>
</div>
);
};

export default Confirmation
